const $contents = document.querySelector("#contents");
const $btnContainer = document.querySelector("#btn-container");

const loadHymn = (event) => {
  const index = event.target.value;
  const $hymn = document.querySelector(".hymn");

  const title = `<div class='hymn__title'><h3>${hymns[index]["title"]}</h3></div>`;
  const text = `<div class='hymn__verses'>${hymns[index]["text"]}</div>`;

  const shareBtn = `<button class='btn' id='share-btn'>Share!</button>`;
  const showChordsBtn = `<button class='btn' id='show-chords-btn'>Toggle Chords</button>`;
  // const btnContainer = `<div id='btn-container'>${showChordsBtn}${shareBtn}</div>`;

  $btnContainer.innerHTML = `${showChordsBtn}${shareBtn}`;
  // $hymn.innerHTML = title + text + "<hr>" + btnContainer;
  $hymn.innerHTML = title + text + "<hr>";
  console.log(getAllVerses());
  btnShareAddListener(
    "*" + $hymn.firstChild.innerText + "*" + "\n\n" + getAllVerses()
  );

  document
    .querySelector("#show-chords-btn")
    .addEventListener("click", toggleChords);
};

let getLineVerses = (line, output) => {
  if (line.className !== "chord") {
    let children = line.childNodes;
    children.forEach((child) => {
      if (child.nodeType === 3) output[0] += child.textContent;
      else getLineVerses(child, output);
    });
  }
};

let getAllVerses = () => {
  let hymn = document.querySelector(".hymn__verses");
  let verses = [""];
  hymn.childNodes.forEach((node) => {
    if (node.nodeName === "BR") verses[0] += "\n";
    else {
      getLineVerses(node, verses);
      verses[0] += "\n";
    }
  });
  return verses[0];
};

const loadContents = () => {
  hymns.forEach((hymn, index) => {
    let option = document.createElement("option");
    option.text = `${hymn["title"]}`;
    option.value = index;
    $contents.appendChild(option);
  });
  $contents.selectedIndex = 0;
};

const btnShareAddListener = (hymnText) => {
  const $shareBtn = document.querySelector("#share-btn");
  $shareBtn.addEventListener("click", () => {
    navigator.share({ text: `${hymnText}` });
  });
};

const toggleChords = (event) => {
  const chords = document.querySelectorAll(".chord");
  chords.forEach((ch) => ch.classList.toggle("hidden"));
};

document.addEventListener("DOMContentLoaded", loadContents);

$contents.addEventListener("change", loadHymn);
